// Esse reducer será responsável por tratar o todas as informações relacionadas as despesas

import { GET_CURRENCY, WALLET_ACTION } from '../actions';

const INITIAL_STATE = {
  isFetching: false,
  currencies: [],
  expenses: [],
  valorTotal: 0,
};

const walletReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case GET_CURRENCY:
    return {
      ...state,
      isFetching: false,
      currencies: action.payload,
    };

  case WALLET_ACTION:
    return {
      ...state,
      expenses: action.payload,
      exchangeRates: action.payloada,
      valorTotal: Number(state.valorTotal) + (Number(action.payload.value)),
      isFetching: false,
    };

  default:
    return state;
  }
};

export default walletReducer;
