import PropTypes from 'prop-types';
import React from 'react';
import { connect } from 'react-redux';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
    };

    this.handleClick = this.handleClick.bind(this);
    this.emailValidation = this.emailValidation.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  emailValidation = () => {
    const { email, password } = this.state;
    const tamanhoDaSenha = 6;
    const validarEmail = (/\S+@\S+\.\S+/).test(email);
    const validarSenha = password.length < tamanhoDaSenha;
    return !validarEmail || validarSenha;
  }

  handleClick = () => {
    const { history, dispatchSetValue } = this.props;
    const { email } = this.state;
    dispatchSetValue(email);
    history.push('/wallet');
  }

  handleChange = ({ target }) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  render() {
    const { email, password } = this.state;
    return (
      <form>
        <label htmlFor="email-input">
          Digite seu email:
          <input
            id="email-input"
            name="email"
            type="email"
            value={ email }
            onChange={ this.handleChange }
            data-testid="email-input"
          />
        </label>
        <label htmlFor="password-input">
          Digite sua senha:
          <input
            id="password-input"
            name="password"
            type="password"
            value={ password }
            onChange={ this.handleChange }
            data-testid="password-input"
          />
        </label>

        <button
          type="button"
          disabled={ this.validarEmail() }
          onClick={ this.handleClick }
        >
          Entrar
        </button>
      </form>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  dispatchSetValue: (value) => dispatch(dispatchSetValue(value)),
});

export default connect(null, mapDispatchToProps)(Login);

Login.propTypes = {
  dispatchSetValue: PropTypes.func.isRequired,
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
};
