import PropTypes from 'prop-types';
import React from 'react';
import { connect } from 'react-redux';
import { currencyAction } from '../actions';
import Header from '../components/Header';

class Wallet extends React.Component {
  constructor() {
    super();
    this.state = {
      id: 0,
      value: 0,
      description: '',
      currency: 'USDT',
      method: 'Dinheiro ',
      tag: 'alimentacao',
    };

    this.handleClick = this.handleClick.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    this.fetchAPI();
  }

  fetchAPI = async () => {
    const { fetchDispatch } = this.props;
    const API = 'https://economia.awesomeapi.com.br/json/all';
    const curr = await fetch(API);
    const response = await curr.json();
    const filtragem = response.filter((moeda) => moeda !== 'USDT')
    fetchDispatch(filtragem);
  }

  handleClick = () => {
    const { value, description, currency } = this.state;
    if (value !== '' && description !== '' && currency !== '') {
      const { fetchDispatch } = this.props;
      let { id } = this.state;
      fetchDispatch(this.state);
      this.setState({
        id: id += 1,
        value: 0,
        description: '',
        currency: '',
        method: 'Dinheiro',
        tag: 'Alimentacao',
      });
    }
  }

  handleChange({ target: { id, value } }) {
    this.setState({
      [id]: value,
    });
  }

  render() {
    const { wallet } = this.props;
    const categoryTag = ['Alimentação', 'Lazer', 'Trabalho', 'Transporte', 'Saúde'];
    const paypal = ['Dinheiro','Cartão de Débito', 'Cartão de Crédito']; 
    const { value, description, currency, method, tag } = this.state;
    console.log(this.props);
    return (
      <div>

        <Header />

        <form>

          <label htmlFor="value">
            Valor da despesa
            <input
              type="number"
              id="value"
              name="value"
              data-testid="value-input"
              onChange={ this.handleChange }
              value={ value }
            />

          </label>
          <label htmlFor="description">
            Descrição da despesa
            <input
              data-testid="description-input"
              name="description"
              type="text"
              onChange={ this.handleChange }
              id="description"
              value={ description }
            />
          </label>

          <label htmlFor="currency">
            Moeda registrada
            <select
              data-testid="currency-input"
              id="currency"
              onChange={ this.handleChange }
              value={ currency }
            >
              {wallet.currencies.map((moeda) => (
                <option
                  key={ moeda }
                  value={ moeda }
                  data-testid={ moeda }
                  id={ moeda }
                >
                  { moeda }
                </option>
              ))}
            </select>
          </label>

          <label htmlFor="method">
            Método de pagamento
            <select
              id="method"
              data-testid="method-input"
              onChange={ this.handleChange }
              value={ method }
            >
              {paypal.map((pagamento) => (
                <option
                  key={ pagamento }
                  value={ pagamento }
                  data-testid={ pagamento }
                  id={ pagamento }
                />
              ))}
            </select>
          </label>

          <label htmlFor="categoria">
            Tipo de despesa
            <select
              data-testid="tag-input"
              id="categoria"
              onChange={ this.handleChange }
              value={ tag }
            >
              {categoryTag.map((item) => (
                <option key={ item } value={ item }>{item}</option>
              ))}
            </select>
          </label>

          <button
            type="button"
            onClick={ this.handleClick }
          >
            Adicionar despesa
          </button>

        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  wallet: state.wallet,
});

const mapDispatchToProps = (dispatch) => ({
  fetchDispatch: (state) => dispatch(currencyAction(state)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Wallet);

Wallet.propTypes = {
  fetchDispatch: PropTypes.func.isRequired,
  wallet: PropTypes.shape({
    currencies: PropTypes.objectOf().isRequired,
    expenses: PropTypes.objectOf().isRequired,
  }).isRequired,
};
