import PropTypes from 'prop-types';
import React from 'react';
import { connect } from 'react-redux';
import { setWallet } from '../actions';

const INITIAL_VALUE = 0;

class Wallet extends React.Component {
  constructor() {
    super();
    this.state = {

    };

    this.handleClick = this.handleClick.bind(this);
  }

  handleClick = () => {
    const { dispatchSetWallet } = this.props;
    dispatchSetWallet();
  }

  render() {
    const { user, wallet } = this.props;
    console.log(this.props);
    return (
      <>
        <div>
          <h1>TrybeWallet</h1>
          <h3 data-testid="email-field">{`Email: ${user.email}`}</h3>
          {wallet.expenses ? (
            <h3 data-testid="total-field">
              {wallet.expenses.reduce((previousValue, currentValue) => (
                previousValue + (currentValue.value * currentValue.filter((value) => {

                }))))}

            </h3>
          ) : (
            <h3 data-testid="total-field">{`Total: ${INITIAL_VALUE}`}</h3>
          )}
          <h3 data-testid="header-currency-field">BRL</h3>
        </div>
        <div>
          <label htmlFor="value-input">
            Valor da despesa
            <input type="number" id="value-input" data-testid="value-input" />
          </label>
          <h3 data-testid="description-input">Descri√ß√£o da despesa</h3>
          <h3 data-testid="currency-input"> Moeda registrada</h3>
          <label
            data-testid="method-input"
            htmlFor="pagamento"
          >
            M√©todo de pagamento
            <select name="pagamento" id="pagamento">
              <option value="dinheiro">Dinheiro</option>
              <option value="cartao-de-credito">Cart√£o de Cr√©dito</option>
              <option value="cartao-de-debito">Cart√£o de D√©bito</option>
            </select>
          </label>
          <label
            data-testid="tag-input"
            htmlFor="categoria"
          >
            Categoria
            <select name="categoria" id="categoria">
              <option value="alimentacao">Alimenta√ß√£o</option>
              <option value="lazer">Lazer</option>
              <option value="trabalho">Trabalho</option>
              <option value="transporte">Transporte</option>
              <option value="saude">Sa√∫de</option>
            </select>
          </label>
          <button type="button" onClick={ this.handleClick }>Adicionar despesa</button>
        </div>
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  user: state.user,
  wallet: state.wallet,
});

const mapDispatchToProps = (dispatch) => ({
  dispatchSetWallet: (value) => dispatch(setWallet(value)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Wallet);

Wallet.propTypes = {
  user: PropTypes.shape({
    email: PropTypes.string.isRequired,
  }).isRequired,
};
