import React from 'react';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      password: '',
    };
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  handleClick = () => {
    const { history, dispatchSetValue } = this.props;
    dispatchSetValue(this.state);
    history.push('/wallet');
  }

  emailValidation = () => {
    const { email, password } = this.state;
    const tamanhoDaSenha = 6;
    const validarEmail = (/\S+@\S+\.\S+/).test(email);
    const validarSenha = password.length < tamanhoDaSenha;
    return !validarEmail || validarSenha;
  }

  render() {
    const { email, password } = this.state;
    return (
      <form>
        <label htmlFor="email-input">
          Digite seu email:
          <input
            id="email-input"
            name="email"
            type="email"
            value={ email }
            data-testid="email-input"
          />
        </label>
        <label htmlFor="password-input">
          Digite sua senha:
          <input
            id="password-input"
            name="password"
            type="password"
            value={ password }
            data-testid="password-input"
          />
        </label>

        <button type="button">Entrar</button>
      </form>
    );
  }
}
export default Login;
