// Esse reducer será responsável por tratar o todas as informações relacionadas as despesas

import { GET_CURRENCY, REQUEST_CURRENCY, WALLET_ACTION } from '../actions';

const INITIAL_STATE = {
  isFetching: false,
  currencies: [],
  expenses: [],
  valorTotal: 0,
};

const walletReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case REQUEST_CURRENCY:
    return {
      ...state,
      isFetching: true,
    };
  case GET_CURRENCY:
    return {
      ...state,
      isFetching: false,
      currencies: [{ ...state.currency, ...action.currency }],
    };
  case WALLET_ACTION:
    return {
      ...state,
      valorTotal: Number(state.valorTotal) + (Number(action.expenses.value)
        * Number(action.exchangeRates[action.expenses.currency].ask)),
      expenses: [...state.expenses,
        { ...action.expenses,
          value: action.expenses.value,
          exchangeRates: action.exchangeRates }],
      isFetching: false,
    };

  default:
    return state;
  }
};

export default walletReducer;
