// Coloque aqui suas actions
export const SET_USER = 'SET_USER';
export const SET_WALLET = 'SET_WALLET';
export const REQUEST_CURRENCY = 'REQUEST_CURRENCY';
export const WALLET_ACTION = 'WALLET_ACTION';
export const GET_CURRENCY = 'GET_CURRENCY';

export const setUser = (payload) => ({
  type: SET_USER,
  payload,
});

export const requestCurrency = () => ({
  type: REQUEST_CURRENCY,
});

export const walletAction = ({ exchangesRates, expenses }) => ({
  type: WALLET_ACTION,
  expenses,
  exchangesRates,
});

export const currencyAction = ({ currency }) => ({
  type: GET_CURRENCY,
  currency,
});

export function fetchCurrency(expenses) {
  return async (dispatch) => {
    dispatch(requestCurrency());
    return fetch('https://economia.awesomeapi.com.br/json/all')
      .then((response) => response.json())
      .then((data) => {
        delete data.USDT;
        return dispatch(walletAction({
          expenses,
          exchangesRates: data,
        }));
      });
  };
}

export function fetchCurrencyDidMount() {
  return async (dispatch) => {
    dispatch(requestCurrency());
    return fetch('https://economia.awesomeapi.com.br/json/all')
      .then((response) => response.json())
      .then((data) => {
        delete data.USDT;
        const keysData = Object.keys(data);
        return dispatch(currencyAction({ currencies: keysData }));
      });
  };
}
